mapper.select:
SELECT
id, code, name, description, created_at, updated_at
FROM role


        AND id = #{id}


        AND code = #{code}


        AND name = #{name}


        AND description = #{description}


        AND created_at = #{createdAt}


        AND updated_at = #{updatedAt}


LIMIT #{pageable.offset}, #{pageable.pageSize}

SELECT count(1)
FROM role


        AND id = #{id}


        AND code = #{code}


        AND name = #{name}


        AND description = #{description}


        AND created_at = #{createdAt}


        AND updated_at = #{updatedAt}
